import React, { Component } from 'react'

export default class NewsItem extends Component {
    render() {
        let { title, discription, src, moreInfo } = this.props;
        console.log(this.props)
        return (
            <div class="card" style="width: 18rem;">
                <ul class="list-group list-group-flush">
                    <img src={src} className="img-fluid rounded-start " alt="..." />
                    <li class="list-group-item">A second item</li>
                    <li class="list-group-item">A third item</li>
                </ul>
                <div class="card-footer">
                    <a href={moreInfo} target='_blank' rel="noreferrer" className="btn btn-warning " style={{ padding: '15px', width: '100%' }}>More Info</a>
                </div>
            </div>
        )
    }
}
