import React, { Component } from 'react'

export default class NewsItem extends Component {
    render() {
        let { title, discription, src, moreInfo } = this.props;
        return (
            <div className="card border border-danger m-4" style={{ width: '20rem' }}>
                <img src={src} className="card-img-top" style={{ height: '40%' }} alt="..." />
                <div className="card-body">
                    <h5 className="card-title">{title}</h5>
                    <p className="card-text">{discription}</p>
                    <a href={moreInfo} target='_blank' rel="noreferrer" className="btn btn-warning " style={{ height: '40px' }}>More Info</a>
                </div>
            </div>
        )
    }
}
