import React, { Component } from 'react'
import alt from './alt.jpg'
export default class NewsItem extends Component {
    render() {
        let { title, discription, src, moreInfo } = this.props;
        console.log(this.props.publish);
        return (
            <div className="card mb-3 m-2 border border-danger" style={{
                maxWidth: '540px'
            }
            }>
                <div className="row g-0">
                    <div className="col-md-4"  >
                        <img src={src ? src : alt} className="img-fluid rounded-start " alt='...' style={{ height: '100%' }} />
                    </div>
                    <div className="col-md-8">
                        <div className="card-body">
                            <h5 className="card-title">{title}</h5>
                            <p className="card-text">{discription}</p>
                            <div className="card-footer">
                                <small className="text-muted">Published On: {this.props.publish.slice(0, 10)} At: {this.props.publish.slice(11, 16)} </small>
                                <a href={moreInfo} target='_blank' rel="noreferrer" className="btn btn-warning " style={{ padding: '15px', width: '100%' }}>More Info</a>
                            </div>
                        </div>
                    </div>
                </div>
            </div >
        )
    }
}
